import React, {useState} from "react";
import { useHistory } from 'react-router-dom';

function Register() {
    const history = useHistory();
    const [isMouseOver, setMouseOver] = useState(false);
    const [buttonDisplay, setButtonDisplay] = useState(false);

    function handleMouseOver() {
        setMouseOver(true);
    }

    function handleMouseOut() {
        setMouseOver(false);
    }

    function handleChange() {
        setButtonDisplay(true);
    }

    return (
    <div className="page-container">
        <h1 className="page-header">Register</h1>
        <form action="/register" method="POST">
            <input type="text" placeholder="User Name" className="login-input" />
            <input type="text" placeholder="Password" className="login-input" onChange={handleChange}/>
            {buttonDisplay === true && (
            <button
                className="login-input signIn"
                style={{backgroundColor: isMouseOver ? "black" : "#E75A25"}}
                onMouseOver={handleMouseOver}
                onMouseOut={handleMouseOut}
                onClick={() => history.push("/login")}
            >Register
            </button> )}
        </form>
    </div>
    )
}

export default Register;
