import { useSelector } from "react-redux";

const planPrice = useSelector(state => state.planPrice);
const repairPrice = useSelector(state => state.repairPrice);

var currentPrice = ({planPrice} + {repairPrice});

const setTotalPriceReducer = (state = 0, action) => {
    switch (action.type) {
        case "UPDATE_TOTAL":
            return state + currentPrice;
        default:
            return state;
    }
  };

export default setTotalPriceReducer;
