import React, { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { chargeBasic, chargePremium } from "../actions";
import { Dropdown } from "react-bootstrap";

function RepairItemMenu(props) {

    const price = useSelector(state => state.price);
    const dispatch = useDispatch();

    const [mainRepair, setMainRepair] = useState(false);

    const [additionalRepair, setAdditionalRepair] = useState(false);


    function mainItemSelected(event) {
        const repairItem = event.target.name;

        props.onAdd(repairItem);

        setMainRepair(true);
    }

    function additionalItemSelected(event) {
        const additionalRepairItem = event.target.name;

        props.onAdd(additionalRepairItem);
    }

    return(
    <div>
    <Dropdown>
        <Dropdown.Toggle
            id="dropdown-basic"
            style={{backgroundColor: "#e75a25"}}
        >
        -- Select Repair Item --
        </Dropdown.Toggle>

        <Dropdown.Menu className="repair-item-menu">
            {mainRepair === false && (
            <Dropdown.Item
                name="No Repair"
                onClick={mainItemSelected}
                >No Repair
            </Dropdown.Item>
            )}
            {mainRepair === false && (
            <Dropdown.Item
                name="Small Chip"
                onClick={mainItemSelected}
                >Small Chip
            </Dropdown.Item>
            )}
            {mainRepair === false && (
            <Dropdown.Item
                name="Big Chip"
                onClick={mainItemSelected}
                >Big Chip
            </Dropdown.Item>
            )}
            {mainRepair === false && (
            <Dropdown.Item
                name="Crack Repair"
                onClick={mainItemSelected}
                >Crack Repair
            </Dropdown.Item>
            )}
            {mainRepair === false && (
            <Dropdown.Item
                name="Crack Stop"
                onClick={mainItemSelected}
                >Crack Stop
            </Dropdown.Item>
            )}
            {mainRepair === true && (
            <Dropdown.Item
                name="Additional Chip"
                onClick={additionalItemSelected}
                >Additional Chip
            </Dropdown.Item>
            )}
            {mainRepair === true && (
            <Dropdown.Item
                name="Additional Syringe"
                onClick={additionalItemSelected}
                >Additional Syringe
            </Dropdown.Item>
            )}
            {mainRepair === true && (
            <Dropdown.Item
                name="Mobile Fee ($20 Charge)"
                onClick={additionalItemSelected}
                >Mobile Fee ($20 Charge)
            </Dropdown.Item>
            )}
        </Dropdown.Menu>
    </Dropdown>
    </div>
    )
}

export default RepairItemMenu;
