import React, {useState} from "react";
import Footer from "./Footer";
import { useHistory } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { chargeBasic, chargePremium, addItemPremium, addItemBasic, addSmallChip, addBigChip, removeSmallChip, removeBigChip } from "../actions";
import ArrowBackIcon from '@material-ui/icons/ArrowBack';
import CheckCircleIcon from '@material-ui/icons/CheckCircle';
import amaLogo from "../images/ama-rewards-logo.png";
import { Dropdown } from "react-bootstrap";
import RepairItemMenu from "./RepairItemMenu";
import RepairItem from "./RepairItem";

function Payment(props) {
    const plan = useSelector(state => state.plan);
    const planPrice = useSelector(state => state.planPrice);
    const repairPrice = useSelector(state => state.repairPrice);
    const renewal = useSelector(state => state.renewal)
    const discount = useSelector(state => state.discount);
    const dispatch = useDispatch();
    const history = useHistory();

    const [isMouseOver, setMouseOver] = useState(false);
    const [discountDisplay, setDiscountDisplay] = useState(false);
    const [amaDisplay, setAmaDisplay] = useState(false);
    const [amaText, setAmaText] = useState("Add");

    const [items, setItems] = useState([]);

    const customerInfo = props.location;

    const totalPrice = {repairPrice}.repairPrice + {planPrice}.planPrice;

    const [customerName, setCustomerName] = useState("");
    const [phoneNumber, setPhoneNumber] = useState("");
    const [licensePlate, setLicensePlate] = useState("");
    const [vehicleInfo, setVehicleInfo] = useState("");

    function handleNameInput(event) {
        setCustomerName(event.target.value);
    }
    function handlePhoneInput(event) {
        setPhoneNumber(event.target.value);
    }
    function handleLicenseInput(event) {
        setLicensePlate(event.target.value);
    }
    function handleVehicleInput(event) {
        setVehicleInfo(event.target.value);
    }

    function addItem (newItem) {
        setItems((prevItems) => {
            return [...prevItems, newItem];
        });

        //Update the Plan Price
        if ({plan}.plan === "Premium") {
            dispatch(addItemPremium());
        } else if ({plan}.plan === "Basic") {
            dispatch(addItemBasic());
        }

        //Update the Repair Price
        if (newItem === "Small Chip") {
            dispatch(addSmallChip());
        } else if (newItem === "Big Chip") {
            dispatch(addBigChip());
        }
    }

    function deleteItem(id, name) {
        console.log(name);
        //call an action to delete
        if (name === "Small Chip") {
            dispatch(removeSmallChip());
        } else if (name === "Big Chip") {
            dispatch(removeBigChip());
        }

        setItems((prevItems) => {
            return prevItems.filter((item, index) => {
                return index !== id;
            });
        });
    }

    function handleDiscountClick() {
        setDiscountDisplay(true);
    }

    function handleAmaClick() {
        console.log(items);
        setAmaDisplay(true);

        if (amaText === "Add") {
            setAmaText("Remove");
        } else {
            setAmaText("Add");
            setAmaDisplay(false);
        }
    }

    return (
    <div className="page-container payment-container">
        <h1 className="page-header payment-header">Payment Information</h1>
        <div className="payment-input-section">
            <div className="info-summary">
                <h1 className="new-plan info-text">New {plan} Plan</h1>
                <label className="info-label">Customer's Full Name</label>
                    <input type="text" placeholder="Full Name" className="info-input-sm" onChange={handleNameInput}/>
                <label className="info-label">Phone Number</label>
                    <input type="text" placeholder="Phone Number" className="info-input-sm" onChange={handlePhoneInput}/>
                <label className="info-label">License Plate</label>
                    <input type="text" placeholder="License Plate" className="info-input-sm" onChange={handleLicenseInput}/>
                <label className="info-label">Vehicle Make & Model</label>
                    <input type="text" placeholder="Vehicle Make & Model" className="info-input-sm" onChange={handleVehicleInput}/>
            </div>
            <div className="repair-item-section">
                <RepairItemMenu
                    onAdd={addItem}
                />
                {items.map((item, index) => {
                    return (
                        <RepairItem
                            key={index}
                            id={index}
                            itemName={item}
                            onDelete={deleteItem}
                        />
                    );
                })}
                <div>
                    Add Another Dropdown Menu for additional items, or use state hooks to conditionally display additional items.
                </div>
            </div>
        </div>
        <div className="payment-summary-section">
            <div className="ama">
                <button
                    className="ama-add"
                    onClick={handleAmaClick}
                    >{amaText}
                    <img className="ama-logo" src={amaLogo} alt="ama-rewards-logo" />
                </button>
                {amaDisplay === true && (
                <div>
                    <label className="ama-label">AMA Number</label>
                        <input type="number" placeholder="620XXXXXXXXXXXXX" className="ama-input"/>
                    <div className="ama-verify">
                    <button
                        className="ama-check">Check AMA Status
                    </button>
                    <CheckCircleIcon fontSize="large" className="ama-status"></CheckCircleIcon>
                    </div>
                </div>
                )}
            </div>
            <div className="detail-summary-section">
                <div className="discount">
                    <div className="discount-add">
                        <button
                            onClick={handleDiscountClick}
                            >Add Discount</button>
                    </div>
                    {discountDisplay === true && (
                        <input type="number" placeholder="0" className="discount-amount"/>
                    )}
                </div>
                <div className="payment-detail">
                    <div className="payment-detail-text">
                        <p>Repair Items Subtotal:</p>
                        <p>Repair Plan Fee:</p>
                        <p>Discount:</p>
                        <p>Taxes:</p>
                        <p><strong>Final Total:</strong></p>
                    </div>
                    <div className="payment-detail-amount">
                        <p>{repairPrice}</p>
                        <p>{planPrice}</p>
                        <p>$0</p>
                        <p>13.0%</p>
                        <p><strong>{totalPrice}</strong></p>
                    </div>
                </div>
            </div>
        </div>
        <button
            className="back-button"
            onClick={() => history.push("/info")}>
            <ArrowBackIcon></ArrowBackIcon>
        </button>
        <Footer />
    </div>
    )
}

export default Payment;
